$(document).ready(function(){$("#slider-holder ul").jcarousel({scroll:1,auto:6,wrap:"both",initCallback:a,itemFirstInCallback:c,buttonNextHTML:null,buttonPrevHTML:null});$(".more-products-holder ul").jcarousel({scroll:2,auto:5,wrap:"both",initCallback:b,buttonNextHTML:null,buttonPrevHTML:null});function a(d){$("#slider-nav a").on("click",function(){var e=$(this).parent().find("a").index(this);d.scroll(e+1);return false})}function c(h,f,d,g){var e=d-1;$("#slider-nav a").removeClass("active");$("#slider-nav a").eq(e).addClass("active")}function b(d){$(".more-nav .next").on("click",function(){d.next();return false});$(".more-nav .prev").on("click",function(){d.prev();return false})}});